}

#mendelian-livetable-container{
  padding: 10px;
}

table.mendelian-results-table{
  border: none;
  border-collapse: separate;
  border-spacing: 0px 5px ;
  text-align: left;
}

tr.patient-row td {
  background-color: white;
  border: none;
  padding: 5px;
  position: relative;
}

