property.code:
var PhenoTips = (function (PhenoTips) {
	var widgets = PhenoTips.widgets = PhenoTips.widgets || {};
	widgets.MendelianSearchFormBehaviour = Class.create({
		initialize : function(){
			this.searchForm = $$('form.mendelian-search-form')[0];
			//Add click listener to toggle advanced options
			this.advancedOptionsButton = $('mendelian-advanced-options-button');
			(this.advancedOptionsButton && this.advancedOptionsButton.observe('click', this.toggleAdvancedOptions));

			//Add term suggest for gene name input
			this.geneSuggest(this.searchForm);

			//Enable term suggest and multi suggest for HPO
			this.HPOSuggest(this.searchForm);
		},

		geneSuggest : function(form) {
		    form.select('input.suggest-genes').each(function(item) {
		        if (!item.hasClassName('initialized')) {
		            var geneServiceURL = new XWiki.Document('GeneNameService', 'PhenoTips').getURL("get", "outputSyntax=plain")
		            //console.log("GeneService URL: " + geneServiceURL);
		            item._suggest = new PhenoTips.widgets.Suggest(item, {
		                script: geneServiceURL + "&json=true&",
		                varname: "q",
		                noresults: "No matching terms",
		                resultsParameter : "docs",
		                json: true,
		                resultId : "symbol",
		                resultValue : "symbol",
		                resultInfo : {},
		                enableHierarchy: false,
		                tooltip : 'gene-info',
		                fadeOnClear : false,
		                timeout : 30000,
		                parentContainer : $('body')
		            });
		            if (item.hasClassName('multi') && typeof(PhenoTips.widgets.SuggestPicker) != "undefined") {
		                item._suggestPicker = new PhenoTips.widgets.SuggestPicker(item, item._suggest, {
		                    'showKey' : false,
		                    'showTooltip' : false,
		                    'showDeleteTool' : true,
		                    'enableSort' : false,
		                    'showClearTool' : true,
		                    'inputType': 'hidden',
		                    'listInsertionElt' : 'input',
		                    'listInsertionPosition' : 'after',
		                    'acceptFreeText' : true
		                });
		            }
		            item.addClassName('initialized');
		            document.observe('ms:suggest:containerCreated', function(event) {
		                if (event.memo && event.memo.suggest === item._suggest) {
		                    item._suggest.container.setStyle({'overflow': 'auto', 'maxHeight': document.viewport.getHeight() - item._suggest.container.cumulativeOffset().top + 'px'})
		                }
		            });
		        }
		    });
		},
	    // HPO terms
	    HPOSuggest : function (form) {
		    form.select('input.suggest-hpo').each(function(item) {
		        if (!item.hasClassName('initialized')) {
		            var solrServiceURL = new XWiki.Document('SolrService', 'PhenoTips').getURL("get", "outputSyntax=plain")
		            //console.log("HPO\SOLR URL: " + solrServiceURL);
		            item._suggest = new PhenoTips.widgets.Suggest(item, {
		                script: solrServiceURL + "rows=100&",
		                queryProcessor: typeof(PhenoTips.widgets.SolrQueryProcessor) == "undefined" ? null : new PhenoTips.widgets.SolrQueryProcessor({
		                    'name' : {'wordBoost': 10, 'phraseBoost': 20},
		                    'nameSpell' : {'wordBoost': 18, 'phraseBoost': 36, 'stubBoost': 14},
		                    'nameExact' : {'phraseBoost': 100},
		                    'namePrefix' : {'phraseBoost': 30},
		                    'synonym' : {'wordBoost': 6, 'phraseBoost': 15},
		                    'synonymSpell' : {'wordBoost': 10, 'phraseBoost': 25, 'stubBoost': 7},
		                    'synonymExact' : {'phraseBoost': 70},
		                    'synonymPrefix' : {'phraseBoost': 20},
		                    'text' : {'wordBoost': 1, 'phraseBoost': 3, 'stubBoost': 1},
		                    'textSpell' : {'wordBoost': 2, 'phraseBoost': 5, 'stubBoost': 2, 'stubTrigger': true},
		                    'id' : {'activationRegex' : /^HP:[0-9]+$/i, 'mandatory' : true, 'transform': function(query) {return query.toUpperCase().replace(/:/g, "\\:");}},
		                    'alt_id' : {'activationRegex' : /^HP:[0-9]+$/i, 'mandatory' : true, 'transform': function(query) {return query.toUpperCase().replace(/:/g, "\\:");}}
		                  }, {
		                    'term_category': ['HP:0000118']
		                  }
		                ),
		                varname: "q",
		                noresults: "No matching terms",
		                json: true,
		                resultsParameter : "rows",
		                resultId : "id",
		                resultValue : "name",
		                resultAltName: "synonym",
		                resultCategory : "term_category",
		                resultInfo : {},
		                enableHierarchy: false,
		                resultParent : "is_a",
		                tooltip: 'phenotype-info',
		                fadeOnClear : false,
		                timeout : 30000,
		                parentContainer : $('body')
		            });
		            if (item.hasClassName('multi') && typeof(PhenoTips.widgets.SuggestPicker) != "undefined") {
		                item._suggestPicker = new PhenoTips.widgets.SuggestPicker(item, item._suggest, {
		                    'showKey' : false,
		                    'showTooltip' : false,
		                    'showDeleteTool' : true,
		                    'enableSort' : false,
		                    'showClearTool' : false,
		                    'inputType': 'hidden',
		                    'listInsertionElt' : $('mendelian-search-symptoms') || 'input',
		                    'listInsertionPosition' : 'bottom',
		                    'acceptFreeText' : false
		                });
		            }
		            item.addClassName('initialized');
		            document.observe('ms:suggest:containerCreated', function(event) {
		                if (event.memo && event.memo.suggest === item._suggest) {
		                    item._suggest.container.setStyle({'overflow': 'auto', 'maxHeight': document.viewport.getHeight() - item._suggest.container.cumulativeOffset().top + 'px'})
		                }
		            });
		        }
		    });
		},

		toggleAdvancedOptions: function(){
			this.advancedOptionsBox = this.advancedOptionsBox || $$("div.mendelian-search-advanced");
			this.advancedOptionsBox.each(function(e){
				if (e && e.hasClassName("collapsed")){
			  		e.removeClassName("collapsed");
			  		this.update('Hide options');
			  	} else if (e) {
			  		e.addClassName("collapsed");
			  		this.update('Advanced options');
			  	}
		  	}.bind(this));
		}
	});

	var init = function (){
		new PhenoTips.widgets.MendelianSearchFormBehaviour();
	};

	(XWiki && XWiki.domIsLoaded && MendelianSearchFormBehaviour()) || document.observe("xwiki:dom:loaded", init);

	// End PhenoTips augmentation.
	return PhenoTips;
}(PhenoTips || {}));

